(4, 150, '#include <iostream>\r\n#include <cstdlib>\r\n#include <ctime>\r\nusing namespace std;\r\n\r\n\r\nint main()\r\n{\r\n	srand(time(0));\r\n	bool p[20];\r\n	\r\n	for (int i = 0; i<20; i++)\r\n	{\r\n		p[i] = rand()%2;\r\n	}\r\n	\r\n	for (int i = 0; i<20; i++)\r\n	{\r\n		cout<<p[i]<<\" \";\r\n	}\r\n	\r\n	//sortiranje izborom\r\n	for (int i = 20; i>0; i--)\r\n	{\r\n		int max = 0;\r\n		for (int j = 0; j<i; j++)\r\n		{\r\n			if (p[j] < p[max])\r\n			{\r\n				max = j;\r\n			}\r\n		}\r\n		\r\n		bool pom = p[i-1];\r\n		p[i-1] = p[max];\r\n		p[max] = pom;\r\n	}\r\n	\r\n	cout<<endl<<\"Ispis nakon sortiranja izborom: \"<<endl;\r\n	for (int i = 0; i<20; i++)\r\n	{\r\n		cout<<p[i]<<\" \";\r\n	}\r\n	\r\n	cout<<endl;\r\n	system(\"pause\");\r\n	return 0;\r\n}\r\n', '5ab2efa659df7fe84b31f0e01e871f5e6c48849717ee515f267362d4cb756e4e', '2025-01-23 17:16:07'),
(5, 277, '#include <iostream>\r\nusing namespace std;\r\n\r\nint main() {\r\n    /* S obzirom na to da se unose prirodni brojevi (pozitivni cijeli brojevi),\r\n    koristimo `unsigned int` jer ono ne podrava negativne vrijednosti.*/\r\n    unsigned int x;\r\n    /* Varijabla `suma_parnih` zapoinje s vrijednou 0 kao neutralan element \r\n    Varijabla `umnozak_neparnih` postavljena je s 1 zbog pravila u mnoenju */\r\n    unsigned int suma_parnih = 0, umnozak_neparnih = 1;\r\n\r\n    /*Logika varijabla `y` koristi se kako bismo pratili je li korisnik unio\r\n    barem jedan pozitivan broj. Ako nije, na kraju programa nema ispisa rezultata.*/\r\n    bool y = false;\r\n\r\n    /*Varijabla `pom` koristi se kao pomona za provjeru unosa negativnih brojeva.\r\n    Iako je `x` tipa `unsigned int`, negativni brojevi unose se kao `int` \r\n    i kasnije se provjeravaju kroz ovu varijablu.*/\r\n    int pom;\r\n\r\n    // Petlja za unos prirodnih brojeva (pozitivnih cijelih brojeva).\r\n    do {\r\n    	//unos prirodnog broja\r\n        cin >> x;\r\n\r\n        /* Da bismo provjerili je li unesen broj negativan (to `unsigned int` ne podrava),\r\n        unos kopiramo u pomonu varijablu `pom` tipa `int`. Ova varijabla omoguuje\r\n        pravilnu provjeru negativnih vrijednosti jer `unsigned int` pretvara negativne brojeve\r\n        u velike pozitivne vrijednosti.*/\r\n        pom = x;\r\n\r\n        // Ako je unesen broj vei od 0, obraujemo ga.\r\n        if (pom > 0) {\r\n            y = true; // Postavljamo `y` na `true`, jer je unesen barem jedan pozitivan broj.\r\n\r\n            // Provjeravamo je li broj paran.\r\n            if (pom % 2 == 0) {\r\n                suma_parnih += pom; // Ako je broj paran, dodajemo ga u zbroj.\r\n            } else {\r\n                umnozak_neparnih *= pom; // Ako je broj neparan, mnoimo ga u umnoak.\r\n            }\r\n        }\r\n\r\n    } while (pom > 0); // Petlja se ponavlja dok se ne unese negativan broj.\r\n\r\n    // Ako je unesen barem jedan pozitivan broj (`y == true`), ispisujemo rezultate.\r\n    if (y) {\r\n        cout << endl << suma_parnih;       // Ispisuje se zbroj parnih brojeva.\r\n        cout << endl << umnozak_neparnih; // Ispisuje se umnoak neparnih brojeva.\r\n        cout << endl;\r\n    }\r\n\r\n    system(\"pause\");\r\n    return 0;\r\n}', 'c2ee59ef348de06054f5ef0ff963376f972ee519850251ae5ba9572cd9a262b1', '2025-01-23 18:40:16'),
(6, 153, '#include<iostream>\r\n#include<cstdlib>\r\n#include<ctime>\r\n\r\nusing namespace std;\r\n\r\nint main() {\r\nsrand(time(0));\r\nchar polje[20];\r\nfor(int i=0; i<20; i++){\r\n	polje[i]=\'a\'+rand()%26;\r\n	cout << polje[i] << \" \";\r\n}\r\nbool zamjena = true;\r\nfor(int i=19; i>0; i--){\r\n	zamjena=false;\r\n	for(int j=0; j<i; j++){\r\n		if(polje[j]>polje[i]){\r\n			char pom=polje[j];\r\n			polje[j]=polje[j+1];\r\n			polje[j+1]=pom;\r\n			zamjena=true;\r\n		}\r\n	}\r\n}\r\n\r\ncout << \"\\n-----------------------------------------------------------------------\\n\";\r\nfor(int i=0; i<20; i++){\r\n	cout << polje[i] << \" \"; \r\n}\r\nreturn 0;\r\n}\r\n', 'dbaacb627139225570940a8d88d4372a02e07a39505b39918101d170b1eb4ba7', '2025-01-24 10:34:01'),
(7, 104, '#include <iostream>\r\n#include <ctime>\r\n#include <cstdlib>\r\nusing namespace std;\r\nint main(){\r\n	srand(time(0));\r\n	int x[15];\r\n	\r\n	for (int i=0; i<15; i++){\r\n		x[i]=rand()%41-20; // Generira broj u rasponu [-20, 20]\r\n		cout<<x[i]; \r\n		if (i!=14) cout<<\", \"; //zarez nakon broja, (osim zadnjeg)\r\n	}\r\n	\r\n	cout<<endl; \r\n	\r\n	for (int i=0; i<15; i++){\r\n		if(x[i]%2) cout<<x[i]<<\" \"; //za ispis neparnih brojeva, nije definirano da trebaju biti odvojeni zarezom pa su odvojeni razmakom\r\n	}\r\n	cout<<endl;\r\n	system(\"pause\");\r\n	return 0;\r\n}\r\n', '9d8e28182fd29962d9c0a0fce526d5838c36c6e28b978a38362030653a8b8409', '2025-01-25 19:02:56'),
(8, 106, '#include <iostream>\r\n#include <ctime>\r\n#include <cstdlib>\r\nusing namespace std;\r\nint main(){\r\n	srand(time(0));\r\n	int x[20];\r\n	\r\n	for (int i=0; i<20; i++){ \r\n		if (i<10){\r\n			x[i]=rand()%21-30; // Generira broj u rasponu [-30, -10] zato  rand()%21 a ne radn()%20 za generiranje 20 brojeva, zato jer poinje od 0 pa treba 1 vie \r\n		}\r\n		else{\r\n			x[i]=rand()%21+10; // Generira broj u rasponu [10, 30] \r\n		}\r\n		\r\n		cout<<x[i];\r\n		if(i<20) cout<<\", \";\r\n	}\r\n	\r\n	cout<<endl; \r\n	\r\n	system(\"pause\");\r\n	return 0;\r\n}', '9d8e28182fd29962d9c0a0fce526d5838c36c6e28b978a38362030653a8b8409', '2025-01-25 19:11:13'),
(9, 116, '#include <iostream>\r\n#include <cstdlib>\r\n#include <ctime>\r\n\r\nusing namespace std;\r\n\r\nint main() {\r\n \r\n\r\n    int a[10]; // Originalni niz\r\n    int b[10]; // Drugi niz za izraune\r\n\r\n    // Generiranje nasuminih brojeva za niz \'a\' i ispis\r\n    cout << \"Originalni niz: \";\r\n    for (int i = 0; i < 10; i++) {\r\n        a[i] = rand() % 9 + 1; // Generira broj u rasponu [1, 9]\r\n        cout << a[i];\r\n        if (i < 9) cout << \", \"; // Dodaje zarez osim za zadnji broj\r\n    }\r\n    cout << endl;\r\n	cout<<\" Drugi niz\";\r\n    // Izraun drugog niza \'b\'\r\n    for (int i = 0; i < 10; i++) {\r\n        // Ako smo na posljednjem elementu, uzmi prvi element kao \"sljedei\"\r\n        int next = (i == 9) ? a[0] : a[i + 1];\r\n\r\n        // Ako je trenutni broj neparan, dodaj dvostruku vrijednost sljedeeg\r\n        if (a[i] % 2 != 0) {\r\n            b[i] = a[i] + 2 * next;\r\n        } \r\n        // Ako je trenutni broj paran, dodaj samo sljedei broj\r\n        else {\r\n            b[i] = a[i] + next;\r\n        }\r\n        \r\n        cout<<b[i]<<\" \";\r\n    }\r\n    cout<<endl;\r\n\r\n    // Pronalazak minimalne vrijednosti u nizu \'b\' i maksimalne vrijednosti u nizu \'a\'\r\n    int min = b[0];\r\n    int max = a[0];\r\n    for (int i = 0; i < 10; i++) {\r\n        if (b[i] < min) min = b[i];\r\n        if (a[i] > max) max = a[i];\r\n    }\r\n\r\n    // Ispis rezultata\r\n    cout << \"Minimalna vrijednost u drugom nizu: \" << min << endl;\r\n    cout << \"Maksimalna vrijednost u originalnom nizu: \" << max << endl;\r\n\r\n    return 0;\r\n}\r\n', '9d8e28182fd29962d9c0a0fce526d5838c36c6e28b978a38362030653a8b8409', '2025-01-26 11:24:31'),
(10, 297, '/*\r\nKreirajte cjelobrojno polje od 5 elemenata te u njega generirajte nasumine brojeve u rasponu od 2 do 8. \r\nNakon toga kreirajte znakovno polje iste veliine te u njega generirajte nasumine simbole + i -. \r\nProite kroz polje sa znakovima i na pozicijama na kojima se nalazi +, na tim pozicijama u polju s brojevima vrijednost poveajte za 1\r\n, a na pozicijama na kojima se nalazi simbol -, na tim pozicijama u polju s brojevima brojeve umanjite za 1.\r\n*/\r\n\r\n#include <iostream>\r\n#include <cstdlib>\r\n#include <ctime>\r\nusing namespace std;\r\nint main(){\r\n	srand(time(0));\r\n	int a[5];\r\n	for (int i=0; i<5; i++){\r\n		a[i]=rand()%7+2;\r\n		cout<<a[i];\r\n	}\r\n	cout<<endl;\r\n	char b[5];\r\n	for(int i=0; i<5; i++){\r\n		if(rand()%2){\r\n			b[i]=\'+\';\r\n		}else{\r\n			b[i]=\'-\';\r\n		}\r\n		cout<<b[i];\r\n	}\r\n	cout<<endl;\r\n	for (int i=0; i<5; i++){\r\n		if(b[i]==\'+\'){\r\n			a[i]+=1;\r\n		}else if(b[i]==\'-\'){\r\n			a[i]-=1;\r\n		}\r\n		cout<<a[i];\r\n	}\r\n	cout<<endl;\r\n	\r\n}', 'c2ee59ef348de06054f5ef0ff963376f972ee519850251ae5ba9572cd9a262b1', '2025-01-27 09:25:53'),
(11, 298, '#include <iostream>\r\n#include <cstdlib>\r\n#include <ctime>\r\n#include <cstring>\r\nusing namespace std;\r\nint main(){\r\n	srand(time(0));\r\n	char a[100];\r\n	cout<<\"Unesite reenicu: \";\r\n	cin.getline(a,100);\r\n	for (int i=0; i<100; i++){\r\n		if(!(a[i]<\'a\'||a[i]>\'z\')){\r\n			if(a[i]!=\' \'){ \r\n			cout<<a[i];				\r\n			}\r\n		}\r\n	}\r\n}', 'c2ee59ef348de06054f5ef0ff963376f972ee519850251ae5ba9572cd9a262b1', '2025-01-27 09:44:08'),
(12, 299, '#include <iostream>\r\n#include <cstdlib>\r\n#include <ctime>\r\n#include <cstring>\r\nusing namespace std;\r\nint main(){\r\n	srand(time(0));\r\n	int a[30];\r\n	for (int i=0; i<30; i++){\r\n		a[i]=rand()%90+10;\r\n		cout<<a[i]<<\" \";\r\n	}\r\n	cout<<endl;\r\n	for (int i=0; i<30; i++){\r\n		if(a[i]<29){\r\n			cout<<a[i]+a[i+1]<<\" \";\r\n		}else{\r\n			cout<<a[i]+a[0]<<\" \";//za zbroj zadnjeg i prvog\r\n		}\r\n	}\r\n	cout<<endl;\r\n}', 'c2ee59ef348de06054f5ef0ff963376f972ee519850251ae5ba9572cd9a262b1', '2025-01-27 10:07:21'),
(13, 282, '#include<iostream>\r\n\r\nusing namespace std;\r\n\r\nint main(){\r\n	int N;\r\n	int zbrojp = 0;\r\n	int zbrojnp = 0;\r\n	do{\r\n		cin >> N;\r\n		if (N % 2 == 0){\r\n			zbrojp += N;\r\n		}\r\n		else if (N%2 != 0){\r\n			zbrojnp += N;\r\n		}\r\n	}while(N!=0);\r\n	cout << \"Zbroj parnih brojeva = \" << zbrojp << endl;\r\n	cout << \"Zbroj neparnih brojeva = \" << zbrojnp << endl;\r\n	cout << endl;\r\n	system(\"pause\");\r\n	return 0;\r\n}\r\n\r\n', '8b16b46ba76bc4ad95ca97d447df70a6fa2317f400e3f021ba38c0427b7cbab2', '2025-01-27 19:05:31'),
(14, 300, '#include<iostream>\r\n#include<ctime>\r\n#include<cstdlib>\r\nusing namespace std;\r\nint main(){\r\n	srand(time(0));\r\n	double a[20];\r\n	for(int i=0; i<20; i++){\r\n		double pom=rand()%900+100;\r\n		a[i]=pom/100;\r\n		cout<<a[i]<<\" \";\r\n	}\r\n	cout<<endl;\r\n	for(int i=1; i<20; i++){\r\n		int j =i-1;\r\n		double pom=a[i];\r\n		while (j>=0 && a[j]>pom){\r\n			a[j+1]=a[j--];\r\n		}\r\n		a[j+1]=pom;\r\n	}\r\n	\r\n	for(int i=0; i<20; i++){\r\n		\r\n		cout<<a[i]<<\" \";\r\n	}\r\n	\r\nsystem(\"pause\");\r\nreturn 0;\r\n}\r\n', '9d8e28182fd29962d9c0a0fce526d5838c36c6e28b978a38362030653a8b8409', '2025-01-27 19:37:32'),
(15, 100, '#include <iostream>\r\n#include <cstdlib>\r\n#include <ctime>\r\nusing namespace std;\r\n\r\nint main() {\r\n    srand(time(0));\r\n    \r\n    char polje[20];\r\n    int samoglasnici = 0;\r\n    int suglasnici = 0;\r\n\r\n    for (int i = 0; i < 20; i++) {\r\n        polje[i] = \'a\' + rand() % 26;\r\n        \r\n        if (polje[i] == \'a\' || polje[i] == \'e\' || polje[i] == \'i\' || polje[i] == \'o\' || polje[i] == \'u\') {\r\n            samoglasnici++;\r\n        } else {\r\n            suglasnici++;\r\n        }\r\n        cout << polje[i] << \" \";\r\n    }\r\n    \r\n    cout << endl << \"Broj samoglasnika: \" << samoglasnici;\r\n    cout << endl << \"Broj suglasnika: \" << suglasnici;\r\n    \r\n    return 0;        \r\n}\r\n', '9de8a79e20b405ddd21216364e8f91852a78f22dcf44eb3e05128486533d3844', '2025-01-28 22:07:11'),
(16, 53, '#include<iostream>\r\nusing namespace std;\r\n\r\nint main()\r\n{\r\n	int broj, brojac = 0;\r\n	\r\n	do{\r\n		cout << \"Unos broja: \" << brojac + 1 << \": \";\r\n		cin >> broj;\r\n		brojac++;\r\n	} while (broj !=0);\r\n	\r\n	cout << \"Unos zavrsen. Ukupno unesenih brojeva (ukljucujuci 0): \" << brojac << endl;\r\n	\r\n	return 0; \r\n}', '01edf4c95a597b8c3bcedea003071556f4ec1040d51bf64a69bd940d678853af', '2025-02-12 15:02:01'),
(17, 149, '#include<iostream>\r\n#include<cstdlib>\r\n#include<ctime>\r\n\r\nusing namespace std;\r\n\r\nint main()\r\n{\r\n	unsigned int brojevi;\r\n	int polje [20];\r\n	srand(time(0));\r\n	int pozitivni = 0, negativni = 0;\r\n	\r\n	\r\n	for( int i = 0; i < 20; i++){\r\n		polje[i] = rand()%21 -10;\r\n		cout << polje[i] << \" \";\r\n		if(polje[i] >= 0){\r\n			pozitivni++;\r\n		}else{\r\n			negativni++;\r\n			}\r\n	}\r\n	\r\n	cout << endl;\r\n	cout << \"Potivnih vrijednosti ukljucujuci nule ima: \" << pozitivni << endl;\r\n	cout << \"Neagtivnih vrijednosti ima: \" << negativni << endl;\r\n	\r\n	system(\"pause\");\r\n	return 0;\r\n}', '01edf4c95a597b8c3bcedea003071556f4ec1040d51bf64a69bd940d678853af', '2025-02-13 16:43:53');
